import React, { useState } from 'react';
import {
  Box,
  Button,
  FormControl,
  FormControlLabel,
  Grid,
  InputAdornment,
  MenuItem,
  Radio,
  RadioGroup,
  TextField,
  Typography,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  CircularProgress
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import SportsIcon from '@mui/icons-material/Sports';

const initialLeg = {
  team: '',
  originalOdds: '',
  currentOdds: '',
  status: 'Pending'
};

const BettingForm = ({ onSubmit, fetchOdds, loading }) => {
  const [formData, setFormData] = useState({
    betType: 'Single',
    stake: '',
    legs: [{ ...initialLeg }],
    cashoutOffer: ''
  });

  const [oddsDialogOpen, setOddsDialogOpen] = useState(false);
  const [currentLegIndex, setCurrentLegIndex] = useState(0);
  const [oddsParams, setOddsParams] = useState({
    sport: 'americanfootball_nfl',
    team: ''
  });
  const [fetchingOdds, setFetchingOdds] = useState(false);
  const [fetchedEvents, setFetchedEvents] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleLegChange = (index, field, value) => {
    const updatedLegs = [...formData.legs];
    updatedLegs[index] = { ...updatedLegs[index], [field]: value };
    
    // If original odds are set and current odds are not, set current = original
    if (field === 'originalOdds' && !updatedLegs[index].currentOdds) {
      updatedLegs[index].currentOdds = value;
    }
    
    setFormData(prev => ({ ...prev, legs: updatedLegs }));
  };

  const addLeg = () => {
    setFormData(prev => ({
      ...prev,
      betType: 'Parlay', // Automatically switch to parlay if adding legs
      legs: [...prev.legs, { ...initialLeg }]
    }));
  };

  const removeLeg = (index) => {
    const updatedLegs = [...formData.legs];
    updatedLegs.splice(index, 1);
    
    setFormData(prev => ({
      ...prev,
      legs: updatedLegs
    }));
    
    // If only one leg is left, switch back to single bet type
    if (updatedLegs.length === 1) {
      setFormData(prev => ({ ...prev, betType: 'Single' }));
    }
  };

  const openOddsDialog = (index) => {
    setCurrentLegIndex(index);
    setOddsParams({
      sport: 'americanfootball_nfl',
      team: formData.legs[index].team
    });
    setFetchedEvents([]);
    setErrorMessage('');
    setOddsDialogOpen(true);
  };

  const handleOddsParamsChange = (e) => {
    const { name, value } = e.target;
    setOddsParams(prev => ({ ...prev, [name]: value }));
  };

  const fetchCurrentOdds = async () => {
    setFetchingOdds(true);
    setErrorMessage('');
    try {
      const events = await fetchOdds(oddsParams);
      setFetchedEvents(events);
      if (events.length === 0) {
        setErrorMessage('No events found for the specified parameters.');
      }
    } catch (error) {
      setErrorMessage(error.message || 'Failed to fetch odds. Please try again.');
    } finally {
      setFetchingOdds(false);
    }
  };

  const selectOdds = (bookmaker, marketKey, outcome) => {
    // Update the leg with the selected odds
    const updatedLegs = [...formData.legs];
    updatedLegs[currentLegIndex] = {
      ...updatedLegs[currentLegIndex],
      currentOdds: outcome.price
    };
    
    setFormData(prev => ({ ...prev, legs: updatedLegs }));
    setOddsDialogOpen(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Validation
    if (!formData.stake || formData.stake <= 0) {
      setErrorMessage('Please enter a valid stake amount.');
      return;
    }
    
    if (!formData.cashoutOffer || formData.cashoutOffer <= 0) {
      setErrorMessage('Please enter a valid cashout offer amount.');
      return;
    }
    
    for (const leg of formData.legs) {
      if (!leg.team) {
        setErrorMessage('Please enter a team/side for all legs.');
        return;
      }
      
      if (!leg.originalOdds) {
        setErrorMessage('Please enter original odds for all legs.');
        return;
      }
      
      if (leg.status === 'Pending' && !leg.currentOdds) {
        setErrorMessage('Please enter current odds for all pending legs.');
        return;
      }
    }
    
    setErrorMessage('');
    onSubmit(formData);
  };

  const sportOptions = [
    { value: 'americanfootball_nfl', label: 'NFL' },
    { value: 'americanfootball_ncaaf', label: 'NCAAF' },
    { value: 'baseball_mlb', label: 'MLB' },
    { value: 'basketball_nba', label: 'NBA' },
    { value: 'basketball_ncaab', label: 'NCAAB' },
    { value: 'icehockey_nhl', label: 'NHL' },
    { value: 'soccer_epl', label: 'EPL' },
    { value: 'soccer_usa_mls', label: 'MLS' }
  ];

  return (
    <Box component="form" onSubmit={handleSubmit} noValidate>
      <Typography variant="h5" gutterBottom>
        Bet Details
      </Typography>
      
      <Grid container spacing={3}>
        {/* Bet Type */}
        <Grid item xs={12} sm={6}>
          <FormControl component="fieldset">
            <Typography variant="subtitle1" gutterBottom>
              Bet Type
            </Typography>
            <RadioGroup
              row
              name="betType"
              value={formData.betType}
              onChange={handleInputChange}
            >
              <FormControlLabel 
                value="Single" 
                control={<Radio />} 
                label="Single" 
                disabled={formData.legs.length > 1}
              />
              <FormControlLabel 
                value="Parlay" 
                control={<Radio />} 
                label="Parlay" 
              />
            </RadioGroup>
          </FormControl>
        </Grid>
        
        {/* Stake */}
        <Grid item xs={12} sm={6}>
          <TextField
            required
            label="Stake"
            name="stake"
            value={formData.stake}
            onChange={handleInputChange}
            fullWidth
            type="number"
            InputProps={{
              startAdornment: <InputAdornment position="start">$</InputAdornment>,
              inputProps: { min: 0, step: 0.01 }
            }}
          />
        </Grid>
      </Grid>
      
      <Typography variant="h6" sx={{ mt: 4, mb: 2 }}>
        Bet Legs
      </Typography>
      
      {formData.legs.map((leg, index) => (
        <Box key={index} sx={{ mb: 3, p: 2, border: '1px solid #e0e0e0', borderRadius: 1 }}>
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={12}>
              <Typography variant="subtitle1">
                Leg {index + 1}
                {formData.legs.length > 1 && (
                  <IconButton 
                    size="small" 
                    onClick={() => removeLeg(index)}
                    sx={{ ml: 1 }}
                    color="error"
                  >
                    <DeleteIcon />
                  </IconButton>
                )}
              </Typography>
            </Grid>
            
            {/* Team */}
            <Grid item xs={12} sm={6}>
              <TextField
                required
                label="Team / Side"
                value={leg.team}
                onChange={(e) => handleLegChange(index, 'team', e.target.value)}
                fullWidth
              />
            </Grid>
            
            {/* Status */}
            <Grid item xs={12} sm={6}>
              <TextField
                select
                label="Status"
                value={leg.status}
                onChange={(e) => handleLegChange(index, 'status', e.target.value)}
                fullWidth
              >
                <MenuItem value="Pending">Pending</MenuItem>
                <MenuItem value="Hit / Won">Hit / Won</MenuItem>
                <MenuItem value="Lost">Lost</MenuItem>
              </TextField>
            </Grid>
            
            {/* Original Odds */}
            <Grid item xs={12} sm={6}>
              <TextField
                required
                label="Original Odds"
                value={leg.originalOdds}
                onChange={(e) => handleLegChange(index, 'originalOdds', e.target.value)}
                fullWidth
                type="number"
                InputProps={{
                  inputProps: { step: 1 }
                }}
                helperText="Enter in American format (e.g. +200, -150)"
              />
            </Grid>
            
            {/* Current Odds (only for pending legs) */}
            {leg.status === 'Pending' && (
              <>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    label="Current Odds"
                    value={leg.currentOdds}
                    onChange={(e) => handleLegChange(index, 'currentOdds', e.target.value)}
                    fullWidth
                    type="number"
                    InputProps={{
                      inputProps: { step: 1 },
                      endAdornment: (
                        <InputAdornment position="end">
                          <IconButton 
                            onClick={() => openOddsDialog(index)}
                            edge="end"
                          >
                            <SportsIcon />
                          </IconButton>
                        </InputAdornment>
                      )
                    }}
                    helperText="Enter manually or fetch from The Odds API"
                  />
                </Grid>
              </>
            )}
          </Grid>
        </Box>
      ))}
      
      <Button
        startIcon={<AddIcon />}
        onClick={addLeg}
        sx={{ mb: 3 }}
        variant="outlined"
      >
        Add Leg
      </Button>
      
      <Typography variant="h6" gutterBottom>
        Cashout Details
      </Typography>
      
      <Grid container spacing={3}>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            label="Cashout Offer"
            name="cashoutOffer"
            value={formData.cashoutOffer}
            onChange={handleInputChange}
            fullWidth
            type="number"
            InputProps={{
              startAdornment: <InputAdornment position="start">$</InputAdornment>,
              inputProps: { min: 0, step: 0.01 }
            }}
          />
        </Grid>
      </Grid>
      
      {errorMessage && (
        <Typography color="error" sx={{ mt: 2 }}>
          {errorMessage}
        </Typography>
      )}
      
      <Button
        type="submit"
        variant="contained"
        color="primary"
        sx={{ mt: 4 }}
        disabled={loading}
      >
        {loading ? <CircularProgress size={24} /> : 'Calculate EV'}
      </Button>
      
      {/* Odds API Dialog */}
      <Dialog open={oddsDialogOpen} onClose={() => setOddsDialogOpen(false)} maxWidth="md" fullWidth>
        <DialogTitle>Fetch Current Odds</DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12} sm={6}>
              <TextField
                select
                label="Sport"
                name="sport"
                value={oddsParams.sport}
                onChange={handleOddsParamsChange}
                fullWidth
              >
                {sportOptions.map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Team (Optional)"
                name="team"
                value={oddsParams.team}
                onChange={handleOddsParamsChange}
                fullWidth
                helperText="Leave blank to see all events"
              />
            </Grid>
          </Grid>
          
          <Button
            variant="contained"
            onClick={fetchCurrentOdds}
            disabled={fetchingOdds}
            sx={{ mt: 2 }}
          >
            {fetchingOdds ? <CircularProgress size={24} /> : 'Fetch Odds'}
          </Button>
          
          {errorMessage && (
            <Typography color="error" sx={{ mt: 2 }}>
              {errorMessage}
            </Typography>
          )}
          
          {fetchedEvents.length > 0 && (
            <Box sx={{ mt: 3 }}>
              <Typography variant="h6" gutterBottom>
                Available Events
              </Typography>
              
              {fetchedEvents.map((event, eventIndex) => (
                <Box key={eventIndex} sx={{ mb: 3, p: 2, border: '1px solid #e0e0e0', borderRadius: 1 }}>
                  <Typography variant="subtitle1" gutterBottom>
                    {event.home_team} vs {event.away_team} ({new Date(event.commence_time).toLocaleString()})
                  </Typography>
                  
                  {event.bookmakers.map((bookmaker, bookmakerIndex) => (
                    <Box key={bookmakerIndex} sx={{ mb: 2 }}>
                      <Typography variant="subtitle2">
                        {bookmaker.title}
                      </Typography>
                      
                      {bookmaker.markets.map((market, marketIndex) => (
                        <Box key={marketIndex} sx={{ ml: 2 }}>
                          <Typography variant="body2" gutterBottom>
                            {market.key === 'h2h' ? 'Moneyline' : market.key}
                          </Typography>
                          
                          <Grid container spacing={1}>
                            {market.outcomes.map((outcome, outcomeIndex) => (
                              <Grid item key={outcomeIndex}>
                                <Button
                                  variant="outlined"
                                  size="small"
                                  onClick={() => selectOdds(bookmaker.key, market.key, outcome)}
                                >
                                  {outcome.name}: {outcome.price}
                                </Button>
                              </Grid>
                            ))}
                          </Grid>
                        </Box>
                      ))}
                    </Box>
                  ))}
                </Box>
              ))}
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOddsDialogOpen(false)}>
            Cancel
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default BettingForm;
