import React, { useState } from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Paper,
  AppBar,
  Toolbar,
  CssBaseline,
  ThemeProvider,
  createTheme
} from '@mui/material';
import BettingForm from './components/BettingForm';
import ResultsDisplay from './components/ResultsDisplay';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

// Create a custom theme
const theme = createTheme({
  palette: {
    primary: {
      main: '#1e88e5',
      light: '#6ab7ff',
      dark: '#005cb2',
      contrastText: '#ffffff',
    },
    secondary: {
      main: '#ff6d00',
      light: '#ff9e40',
      dark: '#c43c00',
      contrastText: '#ffffff',
    },
    background: {
      default: '#f5f5f5',
      paper: '#ffffff',
    },
    success: {
      main: '#43a047',
    },
    warning: {
      main: '#ffa000',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 600,
    },
    h5: {
      fontWeight: 500,
    },
    h6: {
      fontWeight: 500,
    },
  },
  shape: {
    borderRadius: 8,
  },
  components: {
    MuiPaper: {
      styleOverrides: {
        root: {
          boxShadow: '0px 3px 15px rgba(0, 0, 0, 0.1)',
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
          fontWeight: 500,
        },
      },
    },
  },
});

function App() {
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const calculateEV = async (formData) => {
    setLoading(true);
    setError(null);
    
    try {
      const response = await axios.post(`${API_BASE_URL}/calculate-ev`, formData);
      setResults(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'An error occurred calculating expected value');
      console.error('Error calculating EV:', err);
    } finally {
      setLoading(false);
    }
  };

  const fetchOdds = async (params) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/odds`, { params });
      return response.data;
    } catch (err) {
      console.error('Error fetching odds:', err);
      throw new Error(err.response?.data?.message || 'Failed to fetch current odds');
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ 
        minHeight: '100vh',
        background: 'linear-gradient(to bottom right, #f5f7fa, #e4e7eb)',
        flexGrow: 1 
      }}>
        <AppBar position="static" elevation={0} sx={{ backgroundColor: '#ffffff', color: 'primary.main' }}>
          <Toolbar>
            <Typography variant="h5" component="div" sx={{ flexGrow: 1, fontWeight: 'bold' }}>
              ðŸŽ¯ Cashout Calculator
            </Typography>
          </Toolbar>
        </AppBar>
        
        <Container maxWidth="lg" sx={{ mt: 5, mb: 5 }}>
          <Paper sx={{ p: 4, mb: 4, borderRadius: 2 }}>
            <BettingForm 
              onSubmit={calculateEV} 
              fetchOdds={fetchOdds} 
              loading={loading} 
            />
          </Paper>
          
          {error && (
            <Paper sx={{ p: 3, mb: 4, bgcolor: '#ffebee', borderRadius: 2 }}>
              <Typography color="error">{error}</Typography>
            </Paper>
          )}
          
          {results && !error && (
            <Paper sx={{ p: 4, borderRadius: 2 }}>
              <ResultsDisplay results={results} />
            </Paper>
          )}
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
